import React, {useState} from 'react';
import './Login.css';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from "react-redux";
import { register } from '../api/features/users';
function Register() {
    const dispatch = useDispatch();
    const [errors, setErrors] = useState({});

    const [confirmPassword, setConfirmPassword] = useState("");
    const [formData, setFormData] = useState({
      full_name: "",
      email: "",
      password: "",
    });
  
    const { full_name, email, password } = formData;
  
    const onChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };
  
    const onSubmit = (e) => {
      e.preventDefault();
      setErrors({});
  
      if (password !== confirmPassword) {
        setErrors({ confirm_password: "Password and Confirm Password do not match." });
        return;
      }
  
      dispatch(register({ full_name, email, age, password })).then(
        (response) => {
          if (response.error) {
            console.log(response.payload);
            setErrors(response.payload);
          }
        }
      );
    };

  return (
    <div className="wrapper">
        <div className="container wrapper-box">
        <Link className='register-link' to="/"> &lt; Home</Link>
        <h2>Register</h2>
        <div className="form-container">
            <label>Email</label>
            <input
                type="text"
                placeholder="Enter your email"
                autoComplete="off"
            />
            <label>Password</label>
            <input
                type="password"
                placeholder="Enter your password"
                autoComplete="off"
            />
            <label>Confirm Password</label>
            <input
                type="password"
                placeholder="Enter your password"
                autoComplete="off"
            />
        </div>
        <button className='login-button'>Sign Up</button>
        <p>Already have an account? <Link className='register-link' to="/login">Sign In</Link></p>
        
        </div>
    </div>
  )
}

export default Register
